import 'package:flutter/material.dart';import 'package:audioplayers/audioplayers.dart';void main() {  runApp(XylophoneApp());}class XylophoneApp extends StatefulWidget {  const XylophoneApp({super.key});  @override  State<XylophoneApp> createState() => _XylophoneAppState();}class _XylophoneAppState extends State<XylophoneApp> {  void playSound(String fileName) {    final player = AudioPlayer();    player.play(AssetSource(fileName)); // Make sure you have assets configured correctly  }  Expanded createKey(String playSoundName, Color color) { // Using positional parameters    return Expanded(      child: TextButton(        style: TextButton.styleFrom(backgroundColor: color),        onPressed: () {          playSound(playSoundName);        },        child: Text("c1"),      ),    );  }  @override  Widget build(BuildContext context) {    return MaterialApp(      home: Scaffold(        body: SafeArea(          child: Column(            crossAxisAlignment: CrossAxisAlignment.stretch,            children: [              createKey('note1.wav', Colors.red), // Using positional parameters              createKey('note2.wav', Colors.orange),              createKey('note3.wav', Colors.yellow),              createKey('note4.wav', Colors.green),              createKey('note5.wav', Colors.blue),              createKey('note6.wav', Colors.purple),              createKey('note7.wav', Colors.pink),            ],          ),        ),      ),    );  }}